import React from 'react';

const Navbar = () => {
  const menuItems = [
    { id: 'about', label: 'About', hoverColor: 'hover:bg-purple-500' },
    { id: 'skills', label: 'Skills', hoverColor: 'hover:bg-pink-500' },
    { id: 'experience', label: 'Experience', hoverColor: 'hover:bg-indigo-500' },
    { id: 'projects', label: 'Projects', hoverColor: 'hover:bg-teal-500' },
    { id: 'education', label: 'Education', hoverColor: 'hover:bg-yellow-500' },
    { id: 'achievements', label: 'Achievements', hoverColor: 'hover:bg-red-500' },
    { id: 'resume', label: 'Resume', hoverColor: 'hover:bg-green-500' }
  ];

  const scrollToSection = (id) => {
    const section = document.getElementById(id);
    section?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <nav className="bg-[#0a0a23] text-white sticky top-0 z-50 shadow-md">
      <div className="max-w-7xl mx-auto px-4 py-3">
        <ul className="flex flex-wrap justify-around items-center text-sm sm:text-base">
          {menuItems.map((item) => (
            <li
              key={item.id}
              className={`cursor-pointer py-2 px-4 rounded-md transition duration-300 ${item.hoverColor} hover:text-white font-semibold`}
              onClick={() => scrollToSection(item.id)}
            >
              {item.label}
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
