import React from 'react';
import { motion } from 'framer-motion';

export default function About() {
  return (
    <motion.section
      id="about"
      className="max-w-5xl mx-auto p-8 scroll-mt-32"
      initial={{ opacity: 0, y: 20 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
      viewport={{ once: true }}
    >
      <h2 className="text-2xl font-semibold mb-4 text-blue-800 dark:text-yellow-400">About Me</h2>
      <p className="leading-relaxed">
        Detail-oriented Full Stack Developer with 3.9+ years of experience building scalable, user-focused web applications...
      </p>
    </motion.section>
  );
}
